import React, { Component } from "react";
import Search from "./Search";
import MovieList from "./MovieList.js"


class Main extends Component {

  state = {
    movies: [],
    favorites:[]
  }

    saveToLocalStorage = (items) => {
        localStorage.setItem('react-movie-app-favourites', JSON.stringify(items));
    };

    addFavoriteMovie = (movie) => {
        const newFavoriteList = [...this.state.favorites, movie];
        this.setState({
            favorites: newFavoriteList
        })
        this.saveToLocalStorage(newFavoriteList);
    };

    removeFavouriteMovie = (movie) => {
        const newFavoriteList = this.state.favorites.filter(
            (favorite) => favorite.imdbID !== movie.ID
        );

        this.setFavorites(newFavoriteList);
        this.saveToLocalStorage(newFavoriteList);
    };

  searchMovies = (search) => {
    if(search.length > 2){
    fetch(`http://www.omdbapi.com/?s=${search}&apikey=6ec34811`)
      .then(res => res.json())
      .then(movies => {
        this.setState({
          movies: movies.Search,
        })
      })
    }
    else{
        fetch(`http://www.omdbapi.com/?t=${search}&apikey=6ec34811`)
        .then(res => res.json())
        .then(movie => {
          this.setState({
            movies: [movie],
          })
        })
    }
  }

  render() {
    return (
      <div>
        <Search searching = {this.searchMovies} />
        <div>
            <MovieList movies = {this.state.movies} />
            <MovieList movie = {this.state.movies} />     
        </div>
      </div>
    );
  }
}

export default Main;
