import React from "react";

const MovieList = (props) => {

  const Button = (movie) => { 
    let favorite 
    props.favorites.map((m)=> m.imdbID === movie.imdbID? favorite = true: favorite = false)
    return favorite
  }

  return (
    <div>
        {props.movies.map(movie => {
          return <ul><li>{movie.Title} ({movie.Year})</li>
          {Button(movie)
          ?<button onClick={() => props.handleFavorites(movie)}> Nominate </button>
          :<button disabled={"true"} onClick={() => props.handleFavorites(movie)}> Nominate </button>}
          </ul>
        })}
    </div>
  );
};

export default MovieList;
