import React from "react";

const MovieList = (props) => {

  const Button = (movie) => {

    return props.favorites.includes((m)=> m.imbdID === movie.imbdID)
  }

  return (
    <div>
        {props.movies.map(movie => {
          return <ul><li>{movie.Title} ({movie.Year})</li>
          {Button(movie)
          ?<button disabled= 'false'onClick={() => props.handleFavorites(movie)}> Nominate </button>
          :<button disabled="true" onClick={() => props.handleFavorites(movie)}> Nominate </button>}
          </ul>
        })}
    </div>
  );
};

export default MovieList;
